import React, { useState, useEffect } from "react";
import axios from "axios";

const Display = () => {
  const [leads, setLeads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLeads = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/leads");
        setLeads(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to fetch leads");
        setLoading(false);
        console.error("Error details:", err);
      }
    };

    fetchLeads();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Recent Leads</h2>
      <table>
        <thead>
          <tr>
            <th>Lead Number</th>
            <th>Company Name</th>
            <th>IT Contact</th>
            <th>ERP System</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {leads.map((lead) => (
            <tr key={lead._id}>
              <td>{lead.leadNumber}</td>
              <td>{lead.companyInfo.name}</td>
              <td>
                {lead.contactInfo.it.name} ({lead.contactInfo.it.email})
              </td>
              <td>{lead.itLandscape.erp}</td>
              <td>{lead.descriptionSection.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Display;
