import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  companyFormConfig,
  contactFormConfig,
  itLandscapeConfig,
} from "../CreateLeads/formConfigs";
import "./LeadDetails.css"; // Assuming you have CSS for modal

const LeadDetails = ({ leadNumber, onClose }) => {
  const [lead, setLead] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editedLead, setEditedLead] = useState({});
  const [newDescription, setNewDescription] = useState(""); // For adding descriptions
  const [options, setOptions] = useState({});

  useEffect(() => {
    const fetchLeadDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/leads/${leadNumber}`
        );
        setLead(response.data);
        setEditedLead(response.data);
      } catch (err) {
        setError(
          err.message || "An error occurred while fetching lead details"
        );
      } finally {
        setLoading(false);
      }
    };

    fetchLeadDetails();
  }, [leadNumber]);

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const [optionsResponse, userNamesResponse] = await Promise.all([
          axios.get("http://localhost:8080/api/options"),
          axios.get("http://localhost:8080/api/users"),
        ]);
        setOptions((prevOptions) => ({
          ...prevOptions,
          ...optionsResponse.data,
          bdmOptions: userNamesResponse.data,
          leadAssignedToOptions: userNamesResponse.data,
        }));
      } catch (error) {
        console.error("Error fetching options", error);
      }
    };

    fetchOptions();
  }, []);

  const handleInputChange = (e, section, subSection) => {
    const { name, value } = e.target;
    setEditedLead((prevLead) => ({
      ...prevLead,
      [section]: subSection
        ? {
            ...prevLead[section],
            [subSection]: {
              ...prevLead[section]?.[subSection],
              [name]: value || "",
            },
          }
        : { ...prevLead[section], [name]: value || "" },
    }));
  };

  const handleAddDescription = async () => {
    if (!newDescription.trim()) return; // Prevent adding empty descriptions
    try {
      const response = await axios.post(
        `http://localhost:8080/api/leads/${leadNumber}/descriptions`,
        { description: newDescription }
      );
      setLead(response.data);
      setEditedLead(response.data);
      setNewDescription("");
    } catch (err) {
      setError(err.message || "An error occurred while adding a description");
    }
  };

  const handleSave = async () => {
    // Add validation to prevent undefined or null values
    const validEditedLead = { ...editedLead };
    if (!validEditedLead.itLandscape?.SAPInstalledBase) {
      validEditedLead.itLandscape.SAPInstalledBase = [];
    }

    try {
      const response = await axios.put(
        `http://localhost:8080/api/leads/${leadNumber}`,
        validEditedLead
      );
      setLead(response.data);
      setEditMode(false);
    } catch (err) {
      setError(
        err.response?.data?.error || "An error occurred while saving changes"
      );
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!lead) return <div>No lead found</div>;

  const renderFields = (config, section, subSection = null) => {
    return Array.isArray(config)
      ? config.map((row, rowIndex) => (
          <div className="form-row" key={rowIndex}>
            {Array.isArray(row) &&
              row.map((field) => (
                <div className="form-group" key={field.name}>
                  <label>{field.label}:</label>
                  {field.type === "select" ? (
                    <select
                      name={field.name}
                      value={
                        subSection
                          ? editedLead?.[section]?.[subSection]?.[field.name] ||
                            ""
                          : editedLead?.[section]?.[field.name] || ""
                      }
                      onChange={(e) =>
                        handleInputChange(e, section, subSection)
                      }
                    >
                      <option value="">Select {field.label}</option>
                      {options[field.options]?.map((option, index) => (
                        <option key={index} value={option}>
                          {option}
                        </option>
                      ))}
                    </select>
                  ) : (
                    <input
                      type={field.type}
                      name={field.name}
                      value={
                        subSection
                          ? editedLead?.[section]?.[subSection]?.[field.name] ||
                            ""
                          : editedLead?.[section]?.[field.name] || ""
                      }
                      onChange={(e) =>
                        handleInputChange(e, section, subSection)
                      }
                    />
                  )}
                </div>
              ))}
          </div>
        ))
      : null;
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Lead Details - {lead.leadNumber}</h2>
        <button onClick={onClose}>Close</button>
        <button onClick={() => setEditMode(!editMode)}>
          {editMode ? "Cancel" : "Edit"}
        </button>
        {editMode && <button onClick={handleSave}>Save Changes</button>}

        <section>
          <h3>Company Information</h3>
          {renderFields(companyFormConfig, "companyInfo")}
        </section>

        <section>
          <h3>Contact Information</h3>
          {lead.contactInfo && (
            <>
              <div>
                <h4>IT</h4>
                <div className="form-group">
                  <label>Name:</label>
                  {editMode ? (
                    <input
                      type="text"
                      name="itName"
                      value={editedLead?.contactInfo?.it?.name || ""}
                      onChange={(e) =>
                        handleInputChange(e, "contactInfo", "it")
                      }
                    />
                  ) : (
                    <span>{lead?.contactInfo?.it?.name || "N/A"}</span>
                  )}
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  {editMode ? (
                    <input
                      type="email"
                      name="itEmail"
                      value={editedLead?.contactInfo?.it?.email || ""}
                      onChange={(e) =>
                        handleInputChange(e, "contactInfo", "it")
                      }
                    />
                  ) : (
                    <span>{lead?.contactInfo?.it?.email || "N/A"}</span>
                  )}
                </div>
              </div>

              <div>
                <h4>Finance</h4>
                <div className="form-group">
                  <label>Name:</label>
                  {editMode ? (
                    <input
                      type="text"
                      name="financeName"
                      value={editedLead?.contactInfo?.finance?.name || ""}
                      onChange={(e) =>
                        handleInputChange(e, "contactInfo", "finance")
                      }
                    />
                  ) : (
                    <span>{lead?.contactInfo?.finance?.name || "N/A"}</span>
                  )}
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  {editMode ? (
                    <input
                      type="email"
                      name="financeEmail"
                      value={editedLead?.contactInfo?.finance?.email || ""}
                      onChange={(e) =>
                        handleInputChange(e, "contactInfo", "finance")
                      }
                    />
                  ) : (
                    <span>{lead?.contactInfo?.finance?.email || "N/A"}</span>
                  )}
                </div>
              </div>

              <div>
                <h4>Business Head</h4>
                <div className="form-group">
                  <label>Name:</label>
                  {editMode ? (
                    <input
                      type="text"
                      name="businessHeadName"
                      value={editedLead?.contactInfo?.businessHead?.name || ""}
                      onChange={(e) =>
                        handleInputChange(e, "contactInfo", "businessHead")
                      }
                    />
                  ) : (
                    <span>
                      {lead?.contactInfo?.businessHead?.name || "N/A"}
                    </span>
                  )}
                </div>
                <div className="form-group">
                  <label>Email:</label>
                  {editMode ? (
                    <input
                      type="email"
                      name="businessHeadEmail"
                      value={editedLead?.contactInfo?.businessHead?.email || ""}
                      onChange={(e) =>
                        handleInputChange(e, "contactInfo", "businessHead")
                      }
                    />
                  ) : (
                    <span>
                      {lead?.contactInfo?.businessHead?.email || "N/A"}
                    </span>
                  )}
                </div>
              </div>
            </>
          )}
        </section>

        <section>
          <h3>IT Landscape</h3>
          <h4>Net New</h4>
          {renderFields(itLandscapeConfig.netNew, "itLandscape", "netNew")}

          <h4>SAP Installed Base</h4>
          {renderFields(
            itLandscapeConfig.SAPInstalledBase,
            "itLandscape",
            "SAPInstalledBase"
          )}
        </section>
        <section>
          <h3>Descriptions</h3>
          {lead.descriptions &&
            lead.descriptions.map((desc, index) => (
              <div key={index}>
                <p>{desc.description}</p>
                <small>
                  Added on: {new Date(desc.createdAt).toLocaleString()}
                </small>
              </div>
            ))}
          {editMode && (
            <div>
              <textarea
                value={newDescription}
                onChange={(e) => setNewDescription(e.target.value)}
                placeholder="Add a new description"
              />
              <button onClick={handleAddDescription}>Add Description</button>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default LeadDetails;
