import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  companyFormConfig,
  contactFormConfig,
  itLandscapeConfig,
} from "../CreateLeads/formConfigs";
import "./LeadDetails.css";

const FormRow = ({ children }) => <div className="form-row">{children}</div>;

const FormGroup = ({ field, value, onChange, errors }) => (
  <div className="form-group">
    <label htmlFor={field.name}>{field.label}:</label>
    {field.type === "select" ? (
      <select
        name={field.name}
        value={value || ""}
        onChange={onChange}
        className={errors[field.name] ? "mandatory" : ""}
      >
        <option value="">Select {field.label}</option>
        {Array.isArray(field.options) &&
          field.options.map((option, index) => (
            <option key={index} value={option}>
              {option}
            </option>
          ))}
      </select>
    ) : field.type === "textarea" ? (
      <textarea
        name={field.name}
        value={value || ""}
        onChange={onChange}
        className={errors[field.name] ? "mandatory" : ""}
      />
    ) : (
      <input
        type={field.type}
        name={field.name}
        value={value || ""}
        onChange={onChange}
        className={errors[field.name] ? "mandatory" : ""}
      />
    )}
    {errors[field.name] && <span className="error">{errors[field.name]}</span>}
  </div>
);


const LeadDetails = ({ leadNumber, onClose }) => {
  const [lead, setLead] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editedLead, setEditedLead] = useState(null);
  const [errors, setErrors] = useState({});

  useEffect(() => {
    const fetchLeadDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/leads/${leadNumber}`
        );
        setLead(response.data);
        setEditedLead(response.data);
      } catch (err) {
        setError(
          err.message || "An error occurred while fetching lead details"
        );
      } finally {
        setLoading(false);
      }
    };

    fetchLeadDetails();
  }, [leadNumber]);

  const handleChange = (e, section, subSection) => {
    const { name, value } = e.target;
    setEditedLead((prevLead) => {
      if (!prevLead) return null;
      return {
        ...prevLead,
        [section]: subSection
          ? {
              ...prevLead[section],
              [subSection]: {
                ...prevLead[section]?.[subSection],
                [name]: value,
              },
            }
          : { ...prevLead[section], [name]: value },
      };
    });
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(
        `http://localhost:8080/api/leads/${leadNumber}`,
        editedLead
      );
      setLead(response.data);
      setEditMode(false);
    } catch (err) {
      setError(err.message || "An error occurred while saving changes");
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!lead) return <div>No lead found</div>;

  return (
    <div className="lead-details-overlay">
      <div className="lead-details-content">
        <h1>Lead Details - {lead.leadNumber}</h1>
        <button onClick={onClose} className="close-button">
          Close
        </button>
        <button onClick={() => setEditMode(!editMode)} className="edit-button">
          {editMode ? "Cancel" : "Edit"}
        </button>
        {editMode && (
          <button onClick={handleSave} className="save-button">
            Save Changes
          </button>
        )}

        <section className="form-section">
          <h2>Company Information</h2>
          {companyFormConfig.map((row, rowIndex) => (
            <FormRow key={rowIndex}>
              {row.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  value={
                    editMode
                      ? editedLead.companyInfo[field.name]
                      : lead.companyInfo[field.name]
                  }
                  onChange={(e) => handleChange(e, "companyInfo")}
                  errors={errors}
                />
              ))}
            </FormRow>
          ))}
        </section>

        <section className="form-section">
          <h2>Contact Information</h2>
          {contactFormConfig.map((role) => (
            <div key={role.role}>
              <h3>{role.role}</h3>
              {role.fields.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  value={
                    editMode
                      ? editedLead.contactInfo?.[role.role.toLowerCase()]?.[
                          field.name
                        ]
                      : lead.contactInfo?.[role.role.toLowerCase()]?.[
                          field.name
                        ]
                  }
                  onChange={(e) =>
                    handleChange(e, "contactInfo", role.role.toLowerCase())
                  }
                  errors={errors}
                />
              ))}
            </div>
          ))}
        </section>

        <section className="form-section">
          <h2>IT Landscape</h2>
          <h3>Net New</h3>
          {itLandscapeConfig.netNew.map((row, rowIndex) => (
            <FormRow key={`netNew-${rowIndex}`}>
              {row.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  value={
                    editMode
                      ? editedLead.itLandscape?.netNew?.[field.name]
                      : lead.itLandscape?.netNew?.[field.name]
                  }
                  onChange={(e) => handleChange(e, "itLandscape", "netNew")}
                  errors={errors}
                />
              ))}
            </FormRow>
          ))}
          <h3>SAP Installed Base</h3>
          {itLandscapeConfig.SAPInstalledBase.map((row, rowIndex) => (
            <FormRow key={`SAPInstalledBase-${rowIndex}`}>
              {row.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  value={
                    editMode
                      ? editedLead.itLandscape?.SAPInstalledBase?.[field.name]
                      : lead.itLandscape?.SAPInstalledBase?.[field.name]
                  }
                  onChange={(e) =>
                    handleChange(e, "itLandscape", "SAPInstalledBase")
                  }
                  errors={errors}
                />
              ))}
            </FormRow>
          ))}
        </section>

        <section className="form-section">
          <h2>Description Section</h2>
          <div className="form-group full-width">
            <label htmlFor="description">Description:</label>
            <textarea
              id="description"
              name="description"
              value={
                editMode
                  ? editedLead.descriptions?.[0]?.description
                  : lead.descriptions?.[0]?.description
              }
              onChange={(e) => handleChange(e, "descriptions", 0)}
              readOnly={!editMode}
            />
          </div>
          <div className="form-group">
            <label htmlFor="selectedOption">Present Conversation Level:</label>
            <select
              id="selectedOption"
              name="selectedOption"
              value={
                editMode
                  ? editedLead.descriptions?.[0]?.selectedOption
                  : lead.descriptions?.[0]?.selectedOption
              }
              onChange={(e) => handleChange(e, "descriptions", 0)}
              disabled={!editMode}
            >
              <option value="">Select an option</option>
              {/* Add options here */}
            </select>
          </div>
          <div className="form-group">
            <label>Mailer Shared:</label>
            <label>
              <input
                type="radio"
                name="radioValue"
                value="yes"
                checked={
                  (editMode
                    ? editedLead.descriptions?.[0]?.radioValue
                    : lead.descriptions?.[0]?.radioValue) === "yes"
                }
                onChange={(e) => handleChange(e, "descriptions", 0)}
                disabled={!editMode}
              />
              Yes
            </label>
            <label>
              <input
                type="radio"
                name="radioValue"
                value="no"
                checked={
                  (editMode
                    ? editedLead.descriptions?.[0]?.radioValue
                    : lead.descriptions?.[0]?.radioValue) === "no"
                }
                onChange={(e) => handleChange(e, "descriptions", 0)}
                disabled={!editMode}
              />
              No
            </label>
          </div>
        </section>
      </div>
    </div>
  );
};

export default LeadDetails;
