import React, { useState, useEffect } from "react";
import axios from "axios";

const LeadDetails = ({ leadNumber, onClose }) => {
  const [lead, setLead] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editedLead, setEditedLead] = useState(null);
  const [newDescription, setNewDescription] = useState("");

  useEffect(() => {
    const fetchLeadDetails = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/api/leads/${leadNumber}`
        );
        setLead(response.data);
        setEditedLead(response.data);
      } catch (err) {
        setError(
          err.message || "An error occurred while fetching lead details"
        );
      } finally {
        setLoading(false);
      }
    };

    fetchLeadDetails();
  }, [leadNumber]);

  const handleInputChange = (e, section, subSection) => {
    const { name, value } = e.target;
    setEditedLead((prevLead) => {
      if (!prevLead) return null;
      if (section === "companyInfo" || section === "itLandscape") {
        return {
          ...prevLead,
          [section]: {
            ...prevLead[section],
            [subSection]: {
              ...prevLead[section][subSection],
              [name]: value,
            },
          },
        };
      } else if (section === "contactInfo") {
        return {
          ...prevLead,
          contactInfo: {
            ...prevLead.contactInfo,
            [subSection]: {
              ...prevLead.contactInfo[subSection],
              [name]: value,
            },
          },
        };
      }
      return prevLead;
    });
  };

  const handleAddDescription = async () => {
    if (!newDescription.trim()) return;
    try {
      const response = await axios.post(
        `http://localhost:8080/api/leads/${leadNumber}/descriptions`,
        { description: newDescription }
      );
      setLead(response.data);
      setEditedLead(response.data);
      setNewDescription("");
    } catch (err) {
      setError(
        err.message || "An error occurred while adding a new description"
      );
    }
  };

  const handleSave = async () => {
    try {
      const response = await axios.put(
        `http://localhost:8080/api/leads/${leadNumber}`,
        editedLead
      );
      setLead(response.data);
      setEditMode(false);
    } catch (err) {
      setError(
        err.response?.data?.error || "An error occurred while saving changes"
      );
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!lead) return <div>No lead found</div>;

  const renderSection = (sectionName, sectionData) => (
    <div className="section">
      <h3>{sectionName}</h3>
      {Object.entries(sectionData).map(([key, value]) => (
        <div key={key} className="field">
          <label>{key}:</label>
          {editMode ? (
            <input
              type="text"
              name={key}
              value={editedLead[sectionName][key] || ""}
              onChange={(e) => handleInputChange(e, sectionName, key)}
            />
          ) : (
            <span>{value || "N/A"}</span>
          )}
        </div>
      ))}
    </div>
  );

  return (
    <div className="lead-details-box">
      <h2>Lead Details - {lead.leadNumber}</h2>
      <button onClick={onClose}>Close</button>
      <button onClick={() => setEditMode(!editMode)}>
        {editMode ? "Cancel" : "Edit"}
      </button>
      {editMode && <button onClick={handleSave}>Save Changes</button>}

      {renderSection("companyInfo", lead.companyInfo)}

      <div className="section">
        <h3>Contact Information</h3>
        {Object.entries(lead.contactInfo).map(([role, info]) => (
          <div key={role} className="subsection">
            <h4>{role.charAt(0).toUpperCase() + role.slice(1)}</h4>
            {Object.entries(info).map(([key, value]) => (
              <div key={key} className="field">
                <label>{key}:</label>
                {editMode ? (
                  <input
                    type="text"
                    name={key}
                    value={editedLead.contactInfo[role][key] || ""}
                    onChange={(e) => handleInputChange(e, "contactInfo", role)}
                  />
                ) : (
                  <span>{value || "N/A"}</span>
                )}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="section">
        <h3>IT Landscape</h3>
        {Object.entries(lead.itLandscape).map(([section, data]) => (
          <div key={section} className="subsection">
            <h4>{section}</h4>
            {Object.entries(data).map(([key, value]) => (
              <div key={key} className="field">
                <label>{key}:</label>
                {editMode ? (
                  <input
                    type="text"
                    name={key}
                    value={editedLead.itLandscape[section][key] || ""}
                    onChange={(e) =>
                      handleInputChange(e, "itLandscape", section)
                    }
                  />
                ) : (
                  <span>{value || "N/A"}</span>
                )}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="section">
        <h3>Descriptions</h3>
        {lead.descriptions.map((desc, index) => (
          <div key={index} className="description">
            <p>{desc.description}</p>
            <small>Added on: {new Date(desc.createdAt).toLocaleString()}</small>
          </div>
        ))}
        <div className="add-description">
          <textarea
            value={newDescription}
            onChange={(e) => setNewDescription(e.target.value)}
            placeholder="Add a new description"
          />
          <button onClick={handleAddDescription}>Add Description</button>
        </div>
      </div>
    </div>
  );
};

export default LeadDetails;
