import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./CreateLeads.css";

// Import form configs from separate files
import {
  companyFormConfig,
  contactFormConfig,
  itLandscapeConfig,
} from "./formConfigs";

const FormRow = ({ children }) => <div className="form-row">{children}</div>;

const FormGroup = ({ field, formData, handleChange, errors, options }) => (
  <div className="form-group">
    <label htmlFor={field.name}>{field.label}:</label>
    {field.type === "select" ? (
      <div className="select-with-date">
        <select
          name={field.name}
          value={formData[field.name] || ""}
          onChange={handleChange}
          className={errors[field.name] ? "mandatory" : ""}
        >
          <option value="">Select {field.label}</option>
          {options[field.options]?.map((option, index) => (
            <option key={index} value={option}>
              {option}
            </option>
          ))}
        </select>
        {field.datePicker && (
          <input
            type="date"
            name={field.datePicker.name}
            value={formData[field.datePicker.name] || ""}
            onChange={handleChange}
            className={errors[field.datePicker.name] ? "mandatory" : ""}
          />
        )}
      </div>
    ) : (
      <input
        type={field.type}
        id={field.name}
        name={field.name}
        value={formData[field.name] || ""}
        onChange={handleChange}
        className={errors[field.name] ? "mandatory" : ""}
      />
    )}
    {errors[field.name] && <span className="error">{errors[field.name]}</span>}
    {field.datePicker && errors[field.datePicker.name] && (
      <span className="error">{errors[field.datePicker.name]}</span>
    )}
  </div>
);

const CreateLeads = () => {
  const [formData, setFormData] = useState({
    company: {},
    contact: {},
    itLandscape: { netNew: {}, SAPInstalledBase: {} },
    additionalSections: [],
    description: "",
    selectedOption: "",
    radioValue: "",
  });
  const [file, setFile] = useState(null);
  const [errors, setErrors] = useState({});
  const [options, setOptions] = useState({});
  const fileInputRef = useRef(null);

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/options");
        setOptions(response.data);
      } catch (error) {
        console.error("Error fetching options", error);
      }
    };
    fetchOptions();
  }, []);

  const handleChange = (section, e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [section]: { ...prevData[section], [name]: value },
    }));
    if (errors[name]) {
      setErrors((prevErrors) => ({ ...prevErrors, [name]: "" }));
    }
  };

  const handleAdditionalSectionChange = (index, name, value) => {
    setFormData((prevData) => {
      const updatedSections = [...prevData.additionalSections];
      updatedSections[index] = { ...updatedSections[index], [name]: value };
      return { ...prevData, additionalSections: updatedSections };
    });
  };

  const addSection = () => {
    setFormData((prevData) => ({
      ...prevData,
      additionalSections: [...prevData.additionalSections, {}],
    }));
  };

  const resetForm = () => {
    setFormData({
      company: {},
      contact: {},
      itLandscape: { netNew: {}, SAPInstalledBase: {} },
      additionalSections: [],
      description: "",
      selectedOption: "",
      radioValue: "",
    });
    setFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
    setErrors({});
  };

  const validateForm = () => {
    const newErrors = {};
    const validateSection = (config, data) => {
      config.flat().forEach((field) => {
        if (field.required && !data[field.name]?.trim()) {
          newErrors[field.name] = `${field.label} is required`;
        }
        if (field.datePicker?.required && !data[field.datePicker.name]) {
          newErrors[
            field.datePicker.name
          ] = `${field.datePicker.label} is required`;
        }
      });
    };

    validateSection(companyFormConfig, formData.company);
    validateSection(
      contactFormConfig.flatMap((role) => role.fields),
      formData.contact
    );
    Object.entries(itLandscapeConfig).forEach(([section, fields]) => {
      validateSection(fields, formData.itLandscape[section]);
    });

    if (!formData.description.trim())
      newErrors.description = "Description is required";
    if (!file) newErrors.file = "File is required";
    if (!formData.selectedOption)
      newErrors.selectedOption = "Present Conversation Level is required";
    if (!formData.radioValue)
      newErrors.radioValue = "Mailer Shared selection is required";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const formDataToSend = new FormData();
        formDataToSend.append("companyInfo", JSON.stringify(formData.company));
        formDataToSend.append(
          "contactInfo",
          JSON.stringify({
            it: formData.contact,
            finance: formData.contact,
            businessHead: formData.contact,
            otherSections: formData.additionalSections,
          })
        );
        formDataToSend.append(
          "itLandscape",
          JSON.stringify(formData.itLandscape)
        );
        formDataToSend.append("description", formData.description);
        formDataToSend.append("file", file);
        formDataToSend.append("selectedOption", formData.selectedOption);
        formDataToSend.append("radioValue", formData.radioValue);

        const response = await axios.post(
          "http://localhost:8080/api/leads",
          formDataToSend,
          {
            headers: { "Content-Type": "multipart/form-data" },
          }
        );

        alert(
          `Lead created successfully! Lead Number: ${response.data.leadNumber}`
        );
        resetForm();
      } catch (error) {
        console.error("Error saving data", error);
        alert("Error saving data. Please try again.");
      }
    }
  };

  const renderFormSection = (config, section) =>
    config.map((row, rowIndex) => (
      <FormRow key={rowIndex}>
        {row.map((field) => (
          <FormGroup
            key={field.name}
            field={field}
            formData={formData[section]}
            handleChange={(e) => handleChange(section, e)}
            errors={errors}
            options={options}
          />
        ))}
      </FormRow>
    ));

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <section className="form-section">
          <h1>Company Information</h1>
          {renderFormSection(companyFormConfig, "company")}
        </section>

        <section className="form-section">
          <h1>Contact Information</h1>
          {contactFormConfig.map((role, roleIndex) => (
            <div key={roleIndex}>
              <h2>{role.role}</h2>
              {renderFormSection(
                role.fields.reduce((acc, field, index) => {
                  const rowIndex = Math.floor(index / 3);
                  if (!acc[rowIndex]) acc[rowIndex] = [];
                  acc[rowIndex].push(field);
                  return acc;
                }, []),
                "contact"
              )}
            </div>
          ))}
          {formData.additionalSections.map((section, index) => (
            <div key={`other-section-${index}`}>
              <h2>Other Section {index + 1}</h2>
              {renderFormSection(
                contactFormConfig[0].fields.reduce((acc, field, idx) => {
                  const rowIndex = Math.floor(idx / 3);
                  if (!acc[rowIndex]) acc[rowIndex] = [];
                  acc[rowIndex].push({
                    ...field,
                    name: field.name.replace("it", "").toLowerCase(),
                  });
                  return acc;
                }, []),
                "additionalSections"
              )}
            </div>
          ))}
          <div className="form-row">
            <button type="button" className="add-section" onClick={addSection}>
              + Add Other Section
            </button>
          </div>
        </section>

        <section className="form-section">
          <h1>IT Landscape</h1>
          <h2>Net New</h2>
          {renderFormSection(itLandscapeConfig.netNew, "itLandscape")}
          <h2>SAP Installed Base</h2>
          {renderFormSection(itLandscapeConfig.SAPInstalledBase, "itLandscape")}
        </section>

        <section className="form-section">
          <h1>Description Section</h1>
          <div className="form-row">
            <div className="form-group full-width">
              <label htmlFor="description">Description:</label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    description: e.target.value,
                  }))
                }
                required
              />
              {errors.description && (
                <span className="error">{errors.description}</span>
              )}
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="file">Attachment (PDF or Word):</label>
              <input
                type="file"
                id="file"
                name="file"
                accept=".pdf,.doc,.docx"
                onChange={(e) => setFile(e.target.files[0])}
                required
                ref={fileInputRef}
              />
              {errors.file && <span className="error">{errors.file}</span>}
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="option">Present Conversation Level:</label>
              <select
                id="option"
                name="selectedOption"
                value={formData.selectedOption}
                onChange={(e) =>
                  setFormData((prev) => ({
                    ...prev,
                    selectedOption: e.target.value,
                  }))
                }
              >
                <option value="">Select an option</option>
                {options.conversationLevelOptions?.map((option, index) => (
                  <option key={index} value={option}>
                    {option}
                  </option>
                ))}
              </select>
              {errors.selectedOption && (
                <span className="error">{errors.selectedOption}</span>
              )}
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Mailer Shared:</label>
              {["yes", "no"].map((value) => (
                <label key={value}>
                  <input
                    type="radio"
                    name="radioOption"
                    value={value}
                    checked={formData.radioValue === value}
                    onChange={(e) =>
                      setFormData((prev) => ({
                        ...prev,
                        radioValue: e.target.value,
                      }))
                    }
                    required
                  />
                  {value.charAt(0).toUpperCase() + value.slice(1)}
                </label>
              ))}
              {errors.radioValue && (
                <span className="error">{errors.radioValue}</span>
              )}
            </div>
          </div>
          <div className="form-row">
            <button type="submit" className="submit">
              Submit
            </button>
          </div>
        </section>
      </form>
    </div>
  );
};

export default CreateLeads;
