import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./CreateLeads.css";

// Import form configs and helper components from the original files
const companyFormConfig = [
  [
    {
      name: "Lead Type",
      label: "Lead Type",
      type: "select",
      options: "leadTypeOptions",
      required: true,
    },
    { name: "Generic email 1", label: "Generic email 1", type: "email" },
    {
      name: "Vertical",
      label: "Vertical",
      type: "select",
      options: "verticalOptions",
      required: true,
    },
  ],
  [
    {
      name: "Company Name",
      label: "Company Name",
      type: "text",
      required: true,
    },
    { name: "Generic email 2", label: "Generic email 2", type: "email" },
    {
      name: "Lead Assigned to",
      label: "Lead Assigned to",
      type: "select",
      options: "userOptions",
    },
  ],
  [
    { name: "Website", label: "Website", type: "url", required: true },
    { name: "Generic phone 1", label: "Generic phone 1", type: "tel" },
    { name: "BDM", label: "BDM", type: "select", options: "userOptions" },
  ],
  [
    { name: "Address", label: "Address", type: "text", required: true },
    { name: "Generic phone 2", label: "Generic phone 2", type: "tel" },
    {
      name: "Lead Status",
      label: "Lead Status",
      type: "select",
      options: "leadStatusOptions",
    },
  ],
  [
    { name: "City", label: "City", type: "text", required: true },
    {
      name: "Lead Source",
      label: "Lead Source",
      type: "select",
      options: "leadSourceOptions",
    },
    {
      name: "Priority",
      label: "Priority",
      type: "select",
      options: "priorityOptions",
      required: true,
    },
  ],
  [
    {
      name: "State",
      label: "State",
      type: "select",
      options: "stateOptions",
      required: true,
    },
    {
      name: "Total no. of offices",
      label: "Total no. of offices",
      type: "number",
    },
    {
      name: "Next Action",
      label: "Next Action",
      type: "select",
      options: "nextActionOptions",
      required: true,
      datePicker: { name: "dateField", label: "Date" },
    },
  ],
  [
    {
      name: "Country",
      label: "Country",
      type: "select",
      options: "countryOptions",
    },
    {
      name: "Turn Over (INR)",
      label: "Turn Over (INR)",
      type: "select",
      options: "turnOverOptions",
    },
    {
      name: "Lead Usable",
      label: "Lead Usable",
      type: "select",
      options: "leadUsableOptions",
      required: true,
    },
  ],
  [
    {
      name: "Employee Count",
      label: "Employee Count",
      type: "select",
      options: "employeeCountOptions",
    },
    {
      name: "Total no. of Manuf. Units",
      label: "Total no. of Manuf. Units",
      type: "number",
    },
    {
      name: "Reason",
      label: "Reason",
      type: "select",
      options: "reasonOptions",
    },
  ],
  [{ name: "About The Company", label: "About The Company", type: "textarea" }],
];
const contactFormConfig = [
  {
    role: "IT",
    fields: [
      { name: "itName", label: "Name", type: "text", required: true },
      { name: "itDlExt", label: "DL/Ext", type: "text" },
      { name: "itDesignation", label: "Designation", type: "text" },
      { name: "itMobile", label: "Mobile", type: "tel" },
      { name: "itEmail", label: "Email", type: "email", required: true },
      { name: "itPersonalEmail", label: "Personal Email", type: "email" },
    ],
  },
  {
    role: "Finance",
    fields: [
      { name: "financeName", label: "Name", type: "text", required: true },
      { name: "financeDlExt", label: "DL/Ext", type: "text" },
      { name: "financeDesignation", label: "Designation", type: "text" },
      { name: "financeMobile", label: "Mobile", type: "tel" },
      { name: "financeEmail", label: "Email", type: "email", required: true },
      { name: "financePersonalEmail", label: "Personal Email", type: "email" },
    ],
  },
  {
    role: "Business Head",
    fields: [
      { name: "businessHeadName", label: "Name", type: "text", required: true },
      { name: "businessHeadDlExt", label: "DL/Ext", type: "text" },
      { name: "businessHeadDesignation", label: "Designation", type: "text" },
      { name: "businessHeadMobile", label: "Mobile", type: "tel" },
      {
        name: "businessHeadEmail",
        label: "Email",
        type: "email",
        required: true,
      },
      {
        name: "businessHeadPersonalEmail",
        label: "Personal Email",
        type: "email",
      },
    ],
  },
];

const itLandscapeConfig = {
  netNew: [
    [
      {
        name: "Using ERP (y/n)",
        label: "Using ERP (y/n)",
        type: "select",
        options: "usingERPOptions",
      },
      { name: "Budget", label: "Budget", type: "text" },
      {
        name: "Opportunity for us 1",
        label: "Opportunity for us 1",
        type: "select",
        options: "opportunityOptions",
      },
    ],
    [
      {
        name: "If yes, which one",
        label: "If yes, which one",
        type: "select",
        options: "ERPTypeOptions",
      },
      { name: "Authority", label: "Authority", type: "text" },
      {
        name: "Opportunity Value 1",
        label: "Opportunity Value 1",
        type: "select",
      },
    ],
    [
      {
        name: "If no, why",
        label: "If no, why",
        type: "select",
        options: "noWhyOptions",
      },
      { name: "Need", label: "Need", type: "text" },

      {
        name: "Timeframe",
        label: "Timeframe",
        type: "select",
        options: "timeframeOptions",
      },
    ],
    [
      { name: "Hardware", label: "Hardware", type: "text" },

      {
        name: "Current Database",
        label: "Current Database",
        type: "select",
        options: "currentDatabaseOptions",
      },
    ],
  ],
  SAPInstalledBase: [
    [
      {
        name: "Opportunity for us 2",
        label: "Opportunity for us 2",
        type: "select",
        options: "opportunityOptions",
      },
      {
        name: "Year of Implementation",
        label: "Year of Implementation",
        type: "text",
      },
      { name: "No. of Users", label: "No. of Users", type: "number" },
    ],
    [
      {
        name: "Opportunity Value 2",
        label: "Opportunity Value 2",
        type: "text",
      },

      {
        name: "Contract Expiry",
        label: "Contract Expiry",
        type: "select",
        options: "expiryOptions",
      },
      {
        name: "Support Partner",
        label: "Support Partner",
        type: "select",
        options: "partnerOptions",
      },
    ],
    [
      {
        name: "Opportunity for us 3",
        label: "Opportunity for us 3",
        type: "select",
        options: "opportunityOptions",
      },

      {
        name: "Exact Version",
        label: "Exact Version",
        type: "select",
        options: "versionOptions",
      },
      { name: "Hardware", label: "Hardware", type: "text" },
    ],
    [
      {
        name: "Opportunity Value 3",
        label: "Opportunity Value 3",
        type: "text",
      },
      { name: "No. of License", label: "No. of License", type: "number" },
      { name: "License Value", label: "License Value", type: "text" },
    ],
    [
      {
        name: "Modules Implemented",
        label: "Modules Implemented",
        type: "text",
      },

      {
        name: "Implementation Partner",
        label: "Implementation Partner",
        type: "text",
      },
      {
        name: "Total Project Cost",
        label: "Total Project Cost",
        type: "text",
      },
    ],
  ],
};

const FormRow = ({ children }) => <div className="form-row">{children}</div>;

const FormGroup = ({ field, formData, handleChange, errors, options }) => (
  <div className="form-group">
    <label htmlFor={field.name}>{field.label}:</label>
    {field.type === "select" ? (
      <div className="select-with-date">
        <select
          name={field.name}
          value={formData[field.name] || ""}
          onChange={handleChange}
          className={errors[field.name] ? "mandatory" : ""}
        >
          <option value="">Select {field.label}</option>
          {options[field.options] &&
            options[field.options].map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
        </select>
        {field.datePicker && (
          <input
            type="date"
            name={field.datePicker.name}
            value={formData[field.datePicker.name] || ""}
            onChange={handleChange}
            className={errors[field.datePicker.name] ? "mandatory" : ""}
          />
        )}
      </div>
    ) : (
      <input
        type={field.type}
        id={field.name}
        name={field.name}
        value={formData[field.name] || ""}
        onChange={handleChange}
        className={errors[field.name] ? "mandatory" : ""}
      />
    )}
    {errors[field.name] && <span className="error">{errors[field.name]}</span>}
    {field.datePicker && errors[field.datePicker.name] && (
      <span className="error">{errors[field.datePicker.name]}</span>
    )}
  </div>
);

const CreateLeads = () => {
  const [companyFormData, setCompanyFormData] = useState({});
  const [contactFormData, setContactFormData] = useState({});
  const [additionalSections, setAdditionalSections] = useState([]);
  const [itLandscapeData, setItLandscapeData] = useState({
    netNew: {},
    SAPInstalledBase: {},
  });
  const [description, setDescription] = useState("");
  const [file, setFile] = useState(null);
  const [selectedOption, setSelectedOption] = useState("");
  const [radioValue, setRadioValue] = useState("");
  const [errors, setErrors] = useState({});
  const [options, setOptions] = useState({});
  const fileInputRef = useRef(null);
  const [userOptions, setUserOptions] = useState([]);

useEffect(() => {
  const fetchOptions = async () => {
    try {
      const [optionsResponse, usersResponse] = await Promise.all([
        axios.get("http://localhost:8080/api/options"),
        axios.get("http://localhost:8080/api/users/names"),
      ]);
      setOptions(optionsResponse.data);
      setUserOptions(usersResponse.data);
    } catch (error) {
      console.error("Error fetching options or user names", error);
    }
  };
  fetchOptions();
}, []);

 const handleCompanyChange = (e) => {
    const { name, value } = e.target;
    setCompanyFormData({ ...companyFormData, [name]: value });
    if (errors[name]) {
      setErrors({ ...errors, [name]: "" });
    }
  };

  const handleContactChange = (e) => {
    const { name, value } = e.target;
    setContactFormData({ ...contactFormData, [name]: value });
    if (errors[name]) {
      setErrors({ ...errors, [name]: "" });
    }
  };

  const handleAdditionalSectionChange = (index, name, value) => {
    setAdditionalSections((prevSections) => {
      const updatedSections = [...prevSections];
      if (!updatedSections[index]) {
        updatedSections[index] = {};
      }
      updatedSections[index][name] = value;
      return updatedSections;
    });
  };

  const handleItLandscapeChange = (e, section) => {
    const { name, value } = e.target;
    setItLandscapeData({
      ...itLandscapeData,
      [section]: { ...itLandscapeData[section], [name]: value },
    });
    if (errors[name]) {
      setErrors({ ...errors, [name]: "" });
    }
  };

  const addSection = () => {
    setAdditionalSections([...additionalSections, {}]);
  };

  const resetFormFields = () => {
    setCompanyFormData({});
    setContactFormData({});
    setAdditionalSections([]);
    setItLandscapeData({
      netNew: {},
      SAPInstalledBase: {},
    });
    setDescription("");
    setFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
    setSelectedOption("");
    setRadioValue("");
    setErrors({});
  };

  const validateForm = () => {
    const newErrors = {};
    // Validate Company Information
    companyFormConfig.flat().forEach((field) => {
      if (field.required && !companyFormData[field.name]?.trim()) {
        newErrors[field.name] = `${field.label} is required`;
      }
      if (
        field.datePicker &&
        field.datePicker.required &&
        !companyFormData[field.datePicker.name]
      ) {
        newErrors[
          field.datePicker.name
        ] = `${field.datePicker.label} is required`;
      }
    });
    // Validate Contact Information
    contactFormConfig.forEach((role) => {
      role.fields.forEach((field) => {
        if (field.required && !contactFormData[field.name]?.trim()) {
          newErrors[field.name] = `${field.label} is required`;
        }
      });
    });
    // Validate IT Landscape
    Object.entries(itLandscapeConfig).forEach(([section, fields]) => {
      fields.flat().forEach((field) => {
        if (field.required && !itLandscapeData[section][field.name]?.trim()) {
          newErrors[field.name] = `${field.label} is required`;
        }
      });
    });
    // Validate Description Section
    if (!description.trim()) {
      newErrors.description = "Description is required";
    }
    if (!file) {
      newErrors.file = "File is required";
    }
    if (!selectedOption) {
      newErrors.selectedOption = "Present Conversation Level is required";
    }
    if (!radioValue) {
      newErrors.radioValue = "Mailer Shared selection is required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const formData = new FormData();
        formData.append("companyInfo", JSON.stringify(companyFormData));

        // Restructure contact information
        const contactInfo = {
          it: {
            name: contactFormData.itName,
            email: contactFormData.itEmail,
            dlExt: contactFormData.itDlExt,
            designation: contactFormData.itDesignation,
            mobile: contactFormData.itMobile,
            personalEmail: contactFormData.itPersonalEmail,
          },
          finance: {
            name: contactFormData.financeName,
            email: contactFormData.financeEmail,
            dlExt: contactFormData.financeDlExt,
            designation: contactFormData.financeDesignation,
            mobile: contactFormData.financeMobile,
            personalEmail: contactFormData.financePersonalEmail,
          },
          businessHead: {
            name: contactFormData.businessHeadName,
            email: contactFormData.businessHeadEmail,
            dlExt: contactFormData.businessHeadDlExt,
            designation: contactFormData.businessHeadDesignation,
            mobile: contactFormData.businessHeadMobile,
            personalEmail: contactFormData.businessHeadPersonalEmail,
          },
          otherSections: additionalSections,
        };

        formData.append("contactInfo", JSON.stringify(contactInfo));
        formData.append("itLandscape", JSON.stringify(itLandscapeData));
        formData.append("description", description);
        formData.append("file", file);
        formData.append("selectedOption", selectedOption);
        formData.append("radioValue", radioValue);

        const response = await axios.post(
          "http://localhost:8080/api/leads",
          formData,
          {
            headers: { "Content-Type": "multipart/form-data" },
          }
        );

        alert(
          `Lead created successfully! Lead Number: ${response.data.leadNumber}`
        );
        resetFormFields();
      } catch (error) {
        console.error("Error saving data", error);
        alert("Error saving data. Please try again.");
      }
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <section className="form-section">
          <h1>Company Information</h1>
          {companyFormConfig.map((row, rowIndex) => (
            <FormRow key={rowIndex}>
              {row.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  formData={companyFormData}
                  handleChange={handleCompanyChange}
                  errors={errors}
                  options={
                    field.name === "Lead Assigned to" || field.name === "BDM"
                      ? userOptions
                      : options
                  }
                />
              ))}
            </FormRow>
          ))}
        </section>

        <section className="form-section">
          <h1>Contact Information</h1>
          {contactFormConfig.map((role, roleIndex) => (
            <div key={roleIndex}>
              <h2>{role.role}</h2>
              {role.fields
                .reduce((rows, field, index) => {
                  const rowIndex = Math.floor(index / 3);
                  if (!rows[rowIndex]) {
                    rows[rowIndex] = [];
                  }
                  rows[rowIndex].push(
                    <FormGroup
                      key={field.name}
                      field={field}
                      formData={contactFormData}
                      handleChange={handleContactChange}
                      errors={errors}
                      options={options}
                    />
                  );
                  return rows;
                }, [])
                .map((row, rowIndex) => (
                  <FormRow key={rowIndex}>{row}</FormRow>
                ))}
            </div>
          ))}
          {additionalSections.map((section, index) => (
            <div key={`other-section-${index}`}>
              <h2>Other Section {index + 1}</h2>
              {contactFormConfig[0].fields
                .reduce((rows, field, idx) => {
                  const rowIndex = Math.floor(idx / 3);
                  if (!rows[rowIndex]) {
                    rows[rowIndex] = [];
                  }
                  rows[rowIndex].push(
                    <FormGroup
                      key={`${field.name}_other_${index}`}
                      field={{
                        ...field,
                        name: field.name.replace("it", "").toLowerCase(),
                      }}
                      formData={section}
                      handleChange={(e) =>
                        handleAdditionalSectionChange(
                          index,
                          e.target.name,
                          e.target.value
                        )
                      }
                      errors={errors}
                      options={options}
                    />
                  );
                  return rows;
                }, [])
                .map((row, rowIndex) => (
                  <FormRow key={rowIndex}>{row}</FormRow>
                ))}
            </div>
          ))}
          <div className="form-row">
            <button type="button" className="add-section" onClick={addSection}>
              + Add Other Section
            </button>
          </div>
        </section>

        <section className="form-section">
          <h1>IT Landscape</h1>
          <h2>Net New</h2>
          {itLandscapeConfig.netNew.map((row, rowIndex) => (
            <FormRow key={`netNew-${rowIndex}`}>
              {row.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  formData={itLandscapeData.netNew}
                  handleChange={(e) => handleItLandscapeChange(e, "netNew")}
                  errors={errors}
                  options={options}
                />
              ))}
            </FormRow>
          ))}
          <h2>SAP Installed Base</h2>
          {itLandscapeConfig.SAPInstalledBase.map((row, rowIndex) => (
            <FormRow key={`SAPInstalledBase-${rowIndex}`}>
              {row.map((field) => (
                <FormGroup
                  key={field.name}
                  field={field}
                  formData={itLandscapeData.SAPInstalledBase}
                  handleChange={(e) =>
                    handleItLandscapeChange(e, "SAPInstalledBase")
                  }
                  errors={errors}
                  options={options}
                />
              ))}
            </FormRow>
          ))}
        </section>

        <section className="form-section">
          <h1>Description Section</h1>
          <div className="form-row">
            <div className="form-group full-width">
              <label htmlFor="description">Description:</label>
              <textarea
                id="description"
                name="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
              />
              {errors.description && (
                <span className="error">{errors.description}</span>
              )}
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="file">Attachment (PDF or Word):</label>
              <input
                type="file"
                id="file"
                name="file"
                accept=".pdf,.doc,.docx"
                onChange={(e) => setFile(e.target.files[0])}
                required
                ref={fileInputRef}
              />
              {errors.file && <span className="error">{errors.file}</span>}
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="option">Present Conversation Level:</label>
              <select
                id="option"
                name="selectedOption"
                value={selectedOption}
                onChange={(e) => setSelectedOption(e.target.value)}
              >
                <option value="">Select an option</option>
                {options.conversationLevelOptions &&
                  options.conversationLevelOptions.map((option, index) => (
                    <option key={index} value={option}>
                      {option}
                    </option>
                  ))}
              </select>
              {errors.selectedOption && (
                <span className="error">{errors.selectedOption}</span>
              )}
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Mailer Shared:</label>
              <label>
                <input
                  type="radio"
                  name="radioOption"
                  value="yes"
                  checked={radioValue === "yes"}
                  onChange={(e) => setRadioValue(e.target.value)}
                  required
                />
                Yes
              </label>
              <label>
                <input
                  type="radio"
                  name="radioOption"
                  value="no"
                  checked={radioValue === "no"}
                  onChange={(e) => setRadioValue(e.target.value)}
                />
                No
              </label>
              {errors.radioValue && (
                <span className="error">{errors.radioValue}</span>
              )}
            </div>
          </div>
          <div className="form-row">
            <button type="submit" className="submit">
              Submit
            </button>
          </div>
        </section>
      </form>
    </div>
  );
};

export default CreateLeads;
